import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';


class CountryInfo extends React.Component{
	constructor() {
    	super();
    	this.state = { show: false };
  	}

	render(){
		return(
			<div className="countryDetailed" >
				Šeit tiks attēloti papildus dati par katru valsti!
			</div>
		)
	}
}

class CountryRow extends React.Component{
	clickHandle(){
			this.setState({
      		show: !this.state.show
    	});
	}

	render(){
		return(
			<li>
				<div>
					{this.props.value}
				</div>
				<CountryInfo />
			</li>
		);
	}
}

class CountriesList extends React.Component{
	renderContryRow(name) {
    return (
      <CountryRow
        value={name}

      />
    );
  }

	render(){
		return(
			<div className="countriesList">
				<ul>
					{this.renderContryRow('Estonia')}
					{this.renderContryRow('Latvia')}
					{this.renderContryRow('Lithuania')}
				</ul>
			</div>
		);
	}
}


ReactDOM.render(<CountriesList />, document.getElementById('root'));
