import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

import gql from 'graphql-tag'
import { ApolloProvider } from 'react-apollo'
import { ApolloClient } from 'apollo-client'
import { createHttpLink } from 'apollo-link-http'
import { InMemoryCache } from 'apollo-cache-inmemory'

const httpLink = createHttpLink({
  uri: 'https://countries.trevorblades.com'
})

const client = new ApolloClient({
  link: httpLink,
  cache: new InMemoryCache()
})


const FEED_QUERY = gql`
query getCountries{
	countries{
		code
		name
		native
		phone
		continent{
			code
			name
		}
		currency
		languages{
			code
			name
			native
		}
		emoji
		emojiU
	}
}
`

class CountryInfo extends React.Component{
	constructor() {
    	super();
    	this.state = { show: false };
  	}

	render(){
		return(
			<div className="countryDetailed" >
				{this.props.value}
			</div>
		)
	}
}

class CountryRow extends React.Component{
	clickHandle(){
			this.setState({
      		show: !this.state.show
    	});
	}

	render(){
		return(
			<li>
				<div>
					{this.props.value}
				</div>
				<CountryInfo
					value={this.props.key}
					link={this.props.link}
				/>
			</li>
		);
	}
}

class CountriesList extends React.Component{
	renderContryRow(name) {
    return (
			<Query query={FEED_QUERY}>
    		{() => linksToRender.map(link => <CountryInfo key={link.id} link={link} />)}
  		</Query>
      // <CountryRow
      //   value={name}
			//
      // />
    );
  }

	render(){
		return(
			<div className="countriesList">
				<ul>
					{this.renderContryRow('Estonia')}
					{this.renderContryRow('Latvia')}
					{this.renderContryRow('Lithuania')}
				</ul>
			</div>
		);
	}
}


ReactDOM.render(
	<ApolloProvider client={client}>
		<CountriesList />,
	</ApolloProvider>,
	document.getElementById('root')
);
